# Definición del esquema y las tablas

DROP SCHEMA IF EXISTS pokemon;
CREATE SCHEMA pokemon;
USE pokemon;

CREATE TABLE especie (
  n_pokedex INT,
  nombre VARCHAR(12),
  descripcion VARCHAR(200),
  evoluciona INT,
  sprite LONGBLOB,
  PRIMARY KEY (n_pokedex),
  FOREIGN KEY (evoluciona) REFERENCES especie(n_pokedex)
  ON DELETE SET NULL ON UPDATE CASCADE
);

CREATE TABLE ejemplar (
  n_pokedex INT,
  n_encuentro INT,
  apodo VARCHAR(12),
  sexo CHAR(1),
  nivel INT,
  infectado INT,
  PRIMARY KEY (n_pokedex, n_encuentro),
  FOREIGN KEY (n_pokedex) REFERENCES especie(n_pokedex)
  ON DELETE CASCADE ON UPDATE CASCADE
);

CREATE TABLE tipo (
  id_tipo INT,
  nombre VARCHAR(12),
  PRIMARY KEY (id_tipo)
);

CREATE TABLE ataque (
  id_ataque INT,
  nombre VARCHAR(40),
  potencia INT,
  id_tipo INT,
  PRIMARY KEY (id_ataque),
  FOREIGN KEY (id_tipo) REFERENCES tipo(id_tipo)
  ON DELETE CASCADE ON UPDATE CASCADE
);

CREATE TABLE especie_tipo (
  n_pokedex INT,
  id_tipo INT,
  PRIMARY KEY (n_pokedex, id_tipo),
  FOREIGN KEY (n_pokedex) REFERENCES especie(n_pokedex)
  ON DELETE CASCADE ON UPDATE CASCADE,
  FOREIGN KEY (id_tipo) REFERENCES tipo(id_tipo)
  ON DELETE CASCADE ON UPDATE CASCADE
);



