INSERT INTO especie(n_pokedex, nombre, descripcion, evoluciona) VALUES(1, 'Bulbasaur', 'A Bulbasaur es fácil verle echándose una siesta al sol.', NULL);
INSERT INTO especie(n_pokedex, nombre, descripcion, evoluciona) VALUES(2, 'Ivysaur', 'Este Pokémon lleva un bulbo en el lomo y, para poder con su peso, tiene unas patas y un tronco gruesos y fuertes.', 1);
INSERT INTO especie(n_pokedex, nombre, descripcion, evoluciona) VALUES(3, 'Venusaur', 'Venusaur tiene una flor enorme en el lomo que, según parece, adquiere unos colores muy vivos si está bien nutrido y le da mucho el sol.', 2);
INSERT INTO especie(n_pokedex, nombre, descripcion, evoluciona, sprite) VALUES(4, 'Charmander', 'La llama que tiene en la punta de la cola arde según sus sentimientos.', NULL, );
INSERT INTO especie(n_pokedex, nombre, descripcion, evoluciona) VALUES(5, 'Charmeleon', 'Charmeleon no tiene reparo en acabar con su rival usando las afiladas garras que tiene.', 4);
INSERT INTO especie(n_pokedex, nombre, descripcion, evoluciona) VALUES(6, 'Charizard', 'Charizard se dedica a volar por los cielos en busca de oponentes fuertes.', 5);
INSERT INTO especie(n_pokedex, nombre, descripcion, evoluciona) VALUES(7, 'Squirtle', 'El caparazón de Squirtle no le sirve de protección únicamente.', NULL);
INSERT INTO especie(n_pokedex, nombre, descripcion, evoluciona) VALUES(8, 'Wartortle', 'Tiene una cola larga y cubierta de un pelo abundante y grueso que se torna más oscuro a medida que crece.', 7);
INSERT INTO especie(n_pokedex, nombre, descripcion, evoluciona) VALUES(9, 'Blastoise', 'Blastoise lanza chorros de agua con gran precisión por los tubos que le salen del caparazón.', 8);
INSERT INTO especie(n_pokedex, nombre, descripcion, evoluciona) VALUES(133, 'Eevee', 'La configuración genética de Eevee le permite mutar y adaptarse enseguida a cualquier medio en el que viva.', NULL);
INSERT INTO especie(n_pokedex, nombre, descripcion, evoluciona) VALUES(134, 'Vaporeon', 'Vaporeon sufrió una mutación repentina y desarrolló aletas y branquias que le permiten vivir bajo el agua.', 133);
INSERT INTO especie(n_pokedex, nombre, descripcion, evoluciona) VALUES(135, 'Jolteon', 'Las células de Jolteon generan un nivel bajo de electricidad, cuya intensidad aumenta con la electricidad estática que acumula en un pelaje.', 133);
INSERT INTO especie(n_pokedex, nombre, descripcion, evoluciona) VALUES(136, 'Flareon', 'La suavidad del pelaje de Flareon tiene una función clara: libera calor para que el Pokémon no se asfixie.', 133);
